def transform(x):
    if x % 2 == 0:
        return x * 2
    elif x % 3 == 0:
        return x + 3
    else:
        return x - 1

def classify(x):
    if x < 0:
        return -1
    elif x == 0:
        return 0
    else:
        return 1

values = []
for i in range(-20, 30):
    values.append(i)

results = []
for v in values:
    t = transform(v)
    c = classify(t)
    results.append((v, t, c))

positive = 0
negative = 0
zero = 0

for r in results:
    if r[2] == 1:
        positive += 1
    elif r[2] == -1:
        negative += 1
    else:
        zero += 1

total = positive + negative + zero
index = 0

while index < len(results):
    original = results[index][0]
    transformed = results[index][1]
    state = results[index][2]
    if state == 1:
        value = transformed * 2
    elif state == -1:
        value = transformed - 2
    else:
        value = transformed
    results[index] = (original, value, state)
    index += 1

final_sum = 0
for item in results:
    if item[1] > 0:
        final_sum += item[1]
    elif item[1] < 0:
        final_sum -= item[1]
    else:
        final_sum += 0

threshold = final_sum // total
filtered = []

for item in results:
    if item[1] >= threshold:
        filtered.append(item[1])
    else:
        filtered.append(threshold - item[1])

score = 0
for f in filtered:
    if f % 2 == 0:
        score += f // 2
    else:
        score += f

print(score)
